import React from 'react';
import BotCard from './BotCard';

const YourBotArmy = ({ yourBotArmy, onReleaseBot, onDischargeBot }) => {
  return (
    <div>
      <h2>Your Bot Army</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {yourBotArmy.map(bot => (
          <div key={bot.id} style={{ position: 'relative' }}>
            <BotCard bot={bot} />
            <button
              onClick={() => onReleaseBot(bot)}
              style={{ position: 'absolute', top: '10px', right: '10px' }}
            >
              Release
            </button>
            <button
              onClick={() => onDischargeBot(bot)}
              style={{ position: 'absolute', bottom: '10px', right: '10px', backgroundColor: 'red', color: 'white' }}
            >
              x
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default YourBotArmy;
