import React, { useState } from 'react';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/YourBotArmy';
import { deleteBot } from './api';

const App = () => {
  const [yourArmy, setYourArmy] = useState([]);

  const handleEnlist = (bot) => {
    if (yourArmy.find(b => b.bot_class === bot.bot_class)) {
      alert('You already have a bot of this class!');
    } else {
      setYourArmy([...yourArmy, bot]);
    }
  };

  const handleRelease = (bot) => {
    setYourArmy(yourArmy.filter(b => b.id !== bot.id));
  };

  const handleDischarge = async (bot) => {
    await deleteBot(bot.id);
    setYourArmy(yourArmy.filter(b => b.id !== bot.id));
  };

  return (
    <div>
      <BotCollection onEnlist={handleEnlist} />
      <YourBotArmy 
        bots={yourArmy} 
        onRelease={handleRelease} 
        onDischarge={handleDischarge}
      />
    </div>
  );
};

export default App;
