import React from 'react';

const YourBotArmy = ({ yourBotArmy, removeBotFromArmy, dischargeBot }) => {
  return (
    <div className="your-bot-army">
      <h2>Your Bot Army</h2>
      {yourBotArmy.map(bot => (
        <div key={bot.id} className="your-bot-card">
          <h3>{bot.name}</h3>
          <img src={bot.avatar_url} alt={bot.name} />
          <button onClick={() => removeBotFromArmy(bot)}>Remove from Army</button>
          <button onClick={() => dischargeBot(bot.id)} style={{ backgroundColor: 'red' }}>Discharge</button>
        </div>
      ))}
    </div>
  );
};

export default YourBotArmy;
