import React, { useState } from 'react';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/YourBotArmy';

const App = () => {
  const [yourArmy, setYourArmy] = useState([]);

  // Handle enlisting a bot
  const handleEnlist = (bot) => {
    if (!yourArmy.some(b => b.id === bot.id)) {
      setYourArmy([...yourArmy, bot]);
    }
  };

  // Handle releasing a bot
  const handleRelease = (bot) => {
    setYourArmy(yourArmy.filter(b => b.id !== bot.id));
  };

  // Handle discharging a bot (from both your army and the backend)
  const handleDischarge = async (bot) => {
    try {
      await fetch(`http://localhost:8001/bots/${bot.id}`, {
        method: 'DELETE',
      });
      setYourArmy(yourArmy.filter(b => b.id !== bot.id));
    } catch (error) {
      console.error('Failed to discharge bot:', error);
    }
  };

  return (
    <div>
      <h1>Bot Battlr</h1>
      <BotCollection onEnlist={handleEnlist} />
      <YourBotArmy 
        bots={yourArmy} 
        onRelease={handleRelease} 
        onDischarge={handleDischarge}
      />
    </div>
  );
};

export default App;
