import React from 'react';

const BotCard = ({ bot, onAddBot }) => {
  if (!bot) return null; // Ensure bot is defined

  return (
    <div style={{ border: '1px solid black', margin: '10px', padding: '10px', width: '200px' }}>
      <img src={bot.avatar_url} alt={bot.name} style={{ width: '100%' }} />
      <h3>{bot.name}</h3>
      <p>Health: {bot.health}</p>
      <p>Damage: {bot.damage}</p>
      <p>Armor: {bot.armor}</p>
      <button onClick={() => onAddBot(bot)}>Add to Army</button>
    </div>
  );
};

export default BotCard;
