import React from 'react';

const BotCard = ({ bot, addBotToArmy, setSelectedBot }) => {
  const handleClick = () => {
    setSelectedBot(bot);
  };

  return (
    <div className="bot-card" onClick={handleClick}>
      <h2>{bot.name}</h2>
      <img src={bot.avatar_url} alt={bot.name} />
      <p>Class: {bot.bot_class}</p>
    </div>
  );
};

export default BotCard;
