import React, { useEffect, useState } from 'react';
import BotCard from './BotCard';
import { fetchBots } from '../api';

const BotCollection = ({ onEnlist }) => {
  const [bots, setBots] = useState([]); // Initialize as an empty array

  useEffect(() => {
    const loadBots = async () => {
      try {
        const botsData = await fetchBots();
        setBots(botsData);
      } catch (error) {
        console.error('Failed to fetch bots:', error);
      }
    };

    loadBots();
  }, []);

  return (
    <div>
      {/* Add a check to ensure bots is an array */}
      {Array.isArray(bots) && bots.map(bot => (
        <BotCard key={bot.id} bot={bot} onEnlist={onEnlist} />
      ))}
    </div>
  );
};

export default BotCollection;
