import React, { useState, useEffect } from 'react';
import BotCollection from './BotCollection';
import YourBotArmy from './YourBotArmy';
import BotSpecs from './BotSpecs';
import './index.css';

const App = () => {
  const [bots, setBots] = useState([]);
  const [yourBotArmy, setYourBotArmy] = useState([]);
  const [selectedBot, setSelectedBot] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8001/bots')
      .then(response => response.json())
      .then(data => setBots(data));
  }, []);

  const addBotToArmy = (bot) => {
    if (!yourBotArmy.find(b => b.id === bot.id)) {
      setYourBotArmy([...yourBotArmy, bot]);
    }
  };

  const removeBotFromArmy = (bot) => {
    setYourBotArmy(yourBotArmy.filter(b => b.id !== bot.id));
  };

  const dischargeBot = (botId) => {
    fetch(`http://localhost:8001/bots/${botId}`, {
      method: 'DELETE'
    })
      .then(() => {
        setBots(bots.filter(b => b.id !== botId));
        setYourBotArmy(yourBotArmy.filter(b => b.id !== botId));
      });
  };

  return (
    <div className="App">
      <h1>Bot Battlr</h1>
      <div className="content">
        <BotCollection
          bots={bots}
          addBotToArmy={addBotToArmy}
          setSelectedBot={setSelectedBot}
        />
        <YourBotArmy
          yourBotArmy={yourBotArmy}
          removeBotFromArmy={removeBotFromArmy}
          dischargeBot={dischargeBot}
        />
      </div>
      {selectedBot && (
        <BotSpecs
          bot={selectedBot}
          addBotToArmy={addBotToArmy}
          setSelectedBot={setSelectedBot}
        />
      )}
    </div>
  );
};

export default App;
