import React from 'react';

const BotCard = ({ bot, onEnlist, onRelease, onDischarge }) => {
  const handleEnlist = () => onEnlist(bot);
  const handleRelease = () => onRelease(bot);
  const handleDischarge = () => onDischarge(bot);

  return (
    <div>
      <h3>{bot.name}</h3>
      <img src={bot.avatar_url} alt={bot.name} />
      <p>Health: {bot.health}</p>
      <p>Damage: {bot.damage}</p>
      <p>Armor: {bot.armor}</p>
      <p>Class: {bot.bot_class}</p>
      <button onClick={handleEnlist}>Enlist</button>
      {onRelease && <button onClick={handleRelease}>Release</button>}
      {onDischarge && <button onClick={handleDischarge}>Discharge</button>}
    </div>
  );
};

export default BotCard;
