import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/YourBotArmy';

function App() {
  const [bots, setBots] = useState([]);
  const [yourBotArmy, setYourBotArmy] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8001/bots')
      .then(response => setBots(response.data))
      .catch(error => console.error('Error fetching bots:', error));
  }, []);

  const handleAddBot = (bot) => {
    if (!yourBotArmy.find(b => b.id === bot.id)) {
      setYourBotArmy([...yourBotArmy, bot]);
    }
  };

  const handleReleaseBot = (bot) => {
    setYourBotArmy(yourBotArmy.filter(b => b.id !== bot.id));
  };

  const handleDischargeBot = (bot) => {
    axios.delete(`http://localhost:8001/bots/${bot.id}`)
      .then(() => setYourBotArmy(yourBotArmy.filter(b => b.id !== bot.id)))
      .catch(error => console.error('Error deleting bot:', error));
  };

  return (
    <div className="App">
      <h1>Bot Battlr</h1>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <BotCollection bots={bots} onAddBot={handleAddBot} />
        <YourBotArmy
          yourBotArmy={yourBotArmy}
          onReleaseBot={handleReleaseBot}
          onDischargeBot={handleDischargeBot}
        />
      </div>
    </div>
  );
}

export default App;
